$(document).ready(function(){$("#toggle-switcher").click(function(){if($(this).hasClass("opened")){$(this).removeClass("opened");$("#style-switcher").animate({right:"-175px"})}else{$(this).addClass("opened");$("#style-switcher").animate({right:"-15px"})}});$("#style-switcher li").click(function(e){e.preventDefault();var t="styles-"+($(this).index()+1)+".min.css";$("link#theme").attr("href","assets/css/"+t);$("link#theme").load(function(){bgcolor=$(".bg-color2").css("background-color");$("link#main").attr("href","assets/css/"+t);$("#toggle-switcher .fa").css({color:bgcolor})})})})